list.javascript:
function (context, options) {
      if (context.length > 0) {
        var out = "<ul>";
        for(var i = 0,j=context.length; i < j; i++) {
          out += "<li>";
          out += options.fn(context[i]);
          out += "</li>";
        }
        out += "</ul>";
        return out;
      } else {
        return "<p>" + options.inverse(this) + "</p>";
      }
    }

list.php:
function($context, $prev, $scope, $options) {
    if (count($context) > 0) {
        echo '<ul>';
        foreach ($context as $row) {
            echo '<li>';
            $options['fn']($row, $scope);
            echo '</li>';
        }
        echo '</ul>';
    } elseif (isset($options['inverse'])) {
        echo '<p>';
        $options['inverse']($prev, $scope);
        echo '</p>';
    }
}

